    @Override
    public Page<Many2oneC> findAssociatedMany2oneCs(String pid, Pageable pageable) {
        LOGGER.debug("Fetching all associated many2oneCs");

        StringBuilder queryBuilder = new StringBuilder();
        queryBuilder.append("one2manyP.pid = '" + pid + "'");

        return many2oneCService.findAll(queryBuilder.toString(), pageable);
    }

